INSERT INTO movies VALUES(752,'V for Vendetta','In a world in which Great Britain has become a fascist state, a masked vigilante known only as “V” conducts guerrilla warfare against the oppressive British government. When V rescues a young woman from the secret police, he finds in her an ally with whom he can continue his fight to free the people of Britain.','2006-02-23','https://image.tmdb.org/t/p/w500/1avD1JeaRiJX5M4ahPdZPypGoGN.jpg',7.900000000000000355,'Action, Thriller, Science Fiction','en',0);
